mapper.select:
SELECT xt_quartz_log_id, xt_quartz_log_name, xt_quartz_log_key, xt_quartz_log_content, xt_quartz_log_ctime, xt_quartz_log_etime, xt_userinfo_id, xt_quartz_log_flag FROM xt_quartz_log WHERE 1=1
AND xt_quartz_log_name LIKE CONCAT(CONCAT('%',#{xt_quartz_log_name}),'%')
AND xt_quartz_log_ctime >= #{xt_quartz_log_ctime_st}
AND xt_quartz_log_ctime <= #{xt_quartz_log_ctime_et}
AND xt_quartz_log_etime >=#{xt_quartz_log_etime_st}
AND xt_quartz_log_etime <= #{xt_quartz_log_etime_et}
